glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform VARS {
  vec4 u_colorForLoss;
  vec4 u_colorForPreparedness;
  vec2 u_location;
  vec2 resolution;
  float time;
  float u_threshold;
  float u_percentageLoss;
  float u_percentagePrep;
  float u_selected;
  float u_zoom;
  float u_dotSize;
  float u_cellSize;
  float u_randFactor;
  float u_randAlpha;
  float u_sizePower;
  float u_sizeMultiplier;
  float u_stepMin;
  float u_stepMax;
  float u_borderRadius;
};
float rand(vec2 st) {
    return fract(sin(dot(st, vec2(12.839, 78.149))) * 43758.5453);
}
float randForCell(vec2 coord, vec2 offset) {
    vec2 cellCoords = vec2(floor((coord.x + offset.x) / u_cellSize), floor((coord.y + offset.y) / u_cellSize));
    return rand(cellCoords);
}
float cell(vec2 coord, vec2 offset, vec2 shift) {
    vec2 cellCoords = vec2(floor((coord.x + offset.x) / u_cellSize), floor((coord.y + offset.y) / u_cellSize));
    vec2 cellDist = vec2(mod((coord.x + offset.x), u_cellSize), mod((coord.y + offset.y), u_cellSize));
    float r = 1.0 - rand(cellCoords + shift) * u_randFactor;
    cellDist -= u_cellSize / 2.0 * r;
    cellDist = offset - cellDist;
    cellDist /= u_cellSize / 2.0;
    float cd = length(cellDist);
    float size = 1. / (u_sizeMultiplier * pow(r, u_sizePower));
    return 1. - smoothstep(u_stepMin, u_stepMax, cd * size * r);
}
float textureAlpha(vec2 coord) {
    float alpha = 1.-randForCell(-coord, vec2(0., 0.)) * u_randAlpha;
    return alpha * texture(texture, v_uv0).a;
}
float cellIntensity(vec2 coord, vec2 offset, vec2 shift) {
    return cell(coord, offset, shift) * textureAlpha(coord + offset);
}
float dotIntensity(vec2 coord, vec2 shift) {
    coord -= shift;
    float di = 0.;
    di += cellIntensity(coord, vec2(0., 0.), shift);
    di += cellIntensity(coord, vec2(0., u_cellSize), shift);
    di += cellIntensity(coord, vec2(0., -u_cellSize), shift);
    di += cellIntensity(coord, vec2(u_cellSize, 0.), shift);
    di += cellIntensity(coord, vec2(-u_cellSize, 0.), shift);
    di += cellIntensity(coord, vec2(u_cellSize, u_cellSize), shift);
    di += cellIntensity(coord, vec2(u_cellSize, -u_cellSize), shift);
    di += cellIntensity(coord, vec2(-u_cellSize, u_cellSize), shift);
    di += cellIntensity(coord, vec2(-u_cellSize, -u_cellSize), shift);
    return di;
}
vec4 makeBorder(vec2 coord) {
    float radius = (u_borderRadius * u_borderRadius) / (resolution.x * resolution.y);
    vec3 greyDark = vec3(42.0 / 255.0, 54.0 / 255.0, 68.0 / 255.0);
    vec4 accum = texture(texture, (coord.xy));
    accum += texture(texture, (coord.xy + vec2(0., radius)));
    accum += texture(texture, (coord.xy + vec2(0., - radius)));
    accum += texture(texture, (coord.xy + vec2(radius, 0.)));
    accum += texture(texture, (coord.xy + vec2(- radius, 0.)));
    accum += texture(texture, (coord.xy + vec2(- radius, - radius)));
    accum += texture(texture, (coord.xy + vec2(radius, - radius)));
    accum += texture(texture, (coord.xy + vec2(radius, radius)));
    accum += texture(texture, (coord.xy + vec2(- radius, radius)));
    accum /= 9.;
    accum = 1.0 - (abs(0.5 - accum) * 2.);
    accum.rgb = greyDark * accum.a;
    accum *= u_selected;
    return accum;
}
vec4 overlay(vec4 under, vec4 over) {
    return over.a * over + ((1.-length(over.rgb)*over.a) * under);
}
void main () {
  vec4 mask = vec4(0.);
  vec4 o = vec4(1.);
  vec2 uv = v_uv0;
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, uv);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, uv + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  vec4 border = makeBorder(uv);
  vec3 cities = vec3(smoothstep(0.42, 0.42, texture(texture, v_uv0.xy)));
  vec2 st = gl_FragCoord.xy - u_location.xy / u_zoom;
  float di1 = dotIntensity(st, vec2(0., 0.));
  float di2 = dotIntensity(st, vec2(u_cellSize * 0.5, u_cellSize * 0.5));
  float v1 = ( 1.0 - u_percentageLoss / 100.0 );
  v1 = v1 * v1 * v1;
  float v2 = ( 1.0 - u_percentagePrep / 100.0 );
  v2 = v2 * v2 * v2;
  mask = border;
  vec4 color1 = vec4(di1, 0., 0., di1);
  color1 *= 1. - v1;
  vec4 color2 = vec4(0., di2, 0., di2);
  color2 *= 1. - v2;
  mask = overlay(mask, color1);
  mask = overlay(mask, color2);
  ALPHA_TEST(o);
  gl_FragColor = mask;
}

